import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import niloy from 'assets/img/faces/1.jpg'
import $ from 'jquery'

class Login extends Component {
  // clickHandeler = e => {
  //   e.preventDefault()
  //   console.log('niloy')
  // }
  //   componentDidMount () {
  //     $('#login_form').submit(function (event) {
  //       event.preventDefault()
  //       var data = $(this).serializeArray()

  //       $.ajax({
  //         url: 'http://cse299.wp-expert.us:8001/auth/login',
  //         type: 'POST',
  //         data: data,
  //         dataType: 'text json',
  //         success: function (data, textStatus, jqXHR) {
  //           localStorage.setItem('presc_user_token', data.token)
  //           window.location.href = '/components/'
  //         },

  //         error: function (jqXHR, textStatus, errorThrown) {
  //           alert('Login error, try again!')
  //         },

  //         complete: function (jqXHR, textStatus) {
  //           // DO SOMETHING HERE IF YOU WISH TO
  //         }
  //       })
  //     })
  //   }
  render () {
    return (
      <div
        style={{
          backgroundImage: `url(https://images.unsplash.com/photo-1519781542704-957ff19eff00?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=c351475860e8248dcd543ebbba1aa24f&auto=format&fit=crop&w=1446&q=80)`,
          backgroundRepeat: 'no-repeat',
          backgroundSize: 'cover'
        }}
      >
        <div className='container py-5'>
          <div className='row'>
            <div className='col-md-12'>
              <div className='row'>
                <div className='col-md-6 mx-auto'>
                  <div className='card'>
                    <img
                      className='card-img-top'
                      src={niloy}
                      alt='Card image cap'
                    />
                    <div className='card-body'>
                      <h4>
                        Online Prescription Management and Data Collection
                      </h4>

                      <form className='form-group' id='login_form'>
                        <label>Username :</label>
                        <input
                          type='text'
                          name='username'
                          className='form-control'
                          placeholder='Enter Username'
                        />

                        <br />
                        <label>Password :</label>
                        <input
                          type='password'
                          name='password'
                          className='form-control'
                          placeholder='Enter Password'
                        />
                        <br />

                        <center>
                          <Link
                            to='/dashboard'
                            className='btn btn-primary'
                            onClick={this.clickHandeler}
                          >
                            Submit
                          </Link>
                        </center>
                        <br />
                        {/* <div>
                          <center>
                            <Link to='/dsignup'>Register As Doctor</Link> or{' '}
                            <Link to='/psignup'>Register As Paitent</Link>
                          </center>
                          <div>
                            <center>
                              <Link to='/forgetpass'>Forget Password?</Link>
                            </center>
                          </div>
                        </div> */}
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default Login
